    /// @notice Deposit the collateral token for other account
    /// @param to The address of the account to deposit to
    /// @param token The address of collateral token
    /// @param amountX10_D The amount of the token to deposit in decimals D (D = _decimals)
    function depositFor(
        address to,
        address token,
        uint256 amountX10_D
    ) external;

    /// @notice Withdraw collateral from vault
    /// @dev once multi-collateral is implemented, the token is not limited to settlementToken
    /// @param token The address of the token sender is going to withdraw
    /// @param amountX10_D The amount of the token to withdraw in decimals D (D = _decimals)
    function withdraw(address token, uint256 amountX10_D) external;

    /// @notice Liquidate trader's collateral by given settlement token amount
    /// @param trader The address of trader
    /// @param settlementTokenAmountX10_D The amount of settlement token liquidator needs to transfer to Vault
    function liquidateCollateralExactInput(
        address trader,
        address token,
        uint256 settlementTokenAmountX10_D
    ) external returns (uint256 collateralTokenAmount);

    /// @notice Liquidate trader's collateral by given collateral token amount
    /// @param trader The address of trader
    /// @param collateralTokenAmount The amount of collateral token, with decimals same as the collateral token
    function liquidateCollateralExactOutput(
        address trader,
        address token,
        uint256 collateralTokenAmount
    ) external returns (uint256 settlementTokenAmountX10_D);

